$(function() {

    var custom = {

        init: function() {

            this.setLabel();

        },

        setLabel: function() {

            var _this = this;



            var $msItems = $('.ms-item');

            var msItemsLen = $msItems.length;

            var dataArr = [];



            if ($msItems.length > 0) {

                $msItems.each(function(index) {

                    var msid = $(this).attr('id');

                    if (msid && msid != '') {

                        dataArr.push(msid);

                    }

                });



                _this.ajaxRequest(dataArr);

            }

        },

        ajaxRequest: function(dataArr) {

            var _this = this;



            if (dataArr.length <= 0) return;



            var dataStr = dataArr.slice(0, 10).join(',');

            var otherDataArr = dataArr.slice(10);



            $.ajax({

                type: 'GET',

                url: 'http://hd.jumei.com/ajax/get_sellable_byhashids',

                data: {

                    hash_ids: dataStr

                },

                dataType: 'jsonp',

                success: function(data) {

                    $.each(data.data.product, function(index, item) {

                        if (data.data.time > item.start_time) {

                            if (data.data.time > item.end_time || item.sellable == 0) {

                                var selecter = '#' + item.hash_id + ' .san_produc_parent' + ' .ms-sold-out';

                                $(selecter).show();

                            }

                        }

                    });



                    _this.ajaxRequest(otherDataArr);

                },

                error: function(XMLHttpRequest, textStatus, errorThrown) {

                    console.log(XMLHttpRequest.status);

                    console.log(XMLHttpRequest.readyState);

                    console.log(textStatus);

                    console.log(errorThrown);

                },

            });

        }

    }

    custom.init();

});

